namespace RoboRescue.Infrastructure.Templates;

public class Email
{
    public static string EmailTemplate(string title, string content,string username, string code)
    {
        var body =
            "<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\"\n        \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n<html dir=\"ltr\" xmlns=\"http://www.w3.org/1999/xhtml\" xmlns:o=\"urn:schemas-microsoft-com:office:office\" lang=\"\">\n\n<head>\n    <meta charset=\"UTF-8\" content=\"\">\n    <meta content=\"width=device-width, initial-scale=1\" name=\"viewport\">\n    <meta name=\"x-apple-disable-message-reformatting\" content=\"\">\n    <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n    <meta content=\"telephone=no\" name=\"format-detection\">\n    <title></title>\n    <!--[if (mso 16)]>\n    <style type=\"text/css\">\n        a {\n            text-decoration: none;\n        }\n    </style>\n    <![endif]-->\n    <!--[if gte mso 9]>\n    <style>sup {\n        font-size: 100% !important;\n    }</style><![endif]-->\n    <!--[if gte mso 9]>\n    <xml>\n        <o:OfficeDocumentSettings>\n            <o:AllowPNG></o:AllowPNG>\n            <o:PixelsPerInch>96</o:PixelsPerInch>\n        </o:OfficeDocumentSettings>\n    </xml>\n    <![endif]-->\n</head>\n\n<body>\n<div dir=\"ltr\" class=\"es-wrapper-color\">\n    <!--[if gte mso 9]>\n    <v:background xmlns:v=\"urn:schemas-microsoft-com:vml\" fill=\"t\">\n        <v:fill type=\"tile\" color=\"#fafafa\"></v:fill>\n    </v:background>\n    <![endif]-->\n    <table class=\"es-wrapper\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\n        <tbody>\n        <tr>\n            <td class=\"esd-email-paddings\" valign=\"top\">\n                <table cellpadding=\"0\" cellspacing=\"0\" class=\"es-content\" align=\"center\">\n                    <tbody>\n                    <tr>\n                        <td class=\"esd-stripe\" align=\"center\">\n                            <table bgcolor=\"#ffffff\" class=\"es-content-body\" align=\"center\" cellpadding=\"0\"\n                                   cellspacing=\"0\" width=\"600\">\n                                <tbody>\n                                <tr>\n                                    <td class=\"esd-structure es-p30t es-p30b es-p20r es-p20l\" align=\"left\">\n                                        <table cellpadding=\"0\" cellspacing=\"0\" width=\"100%\">\n                                            <tbody>\n                                            <tr>\n                                                <td width=\"560\" class=\"esd-container-frame\" align=\"center\" valign=\"top\">\n                                                    <table cellpadding=\"0\" cellspacing=\"0\" width=\"100%\">\n                                                        <tbody>\n                                                        <tr>\n                                                            <td align=\"center\" class=\"esd-block-image es-p10t es-p10b\"\n                                                                style=\"font-size: 0px;\"><a target=\"_blank\"><img\n                                                                    src=\"\"\n                                                                    style=\"display: block;\" width=\"100\" alt=\"\"></a></td>\n                                                        </tr>\n                                                        <tr>\n                                                            <td align=\"center\"\n                                                                class=\"esd-block-text es-p10b es-m-txt-c\">\n                                                                <h1 style=\"font-size: 46px; line-height: 100%;\">[title]</h1>\n                                                            </td>\n                                                        </tr>\n                                                        <tr>\n                                                            <td align=\"center\"\n                                                                class=\"esd-block-text es-p5t es-p5b es-p40r es-p40l es-m-p0r es-m-p0l\">\n                                                                <p>Hello [username],\n                                                                    [body].</p>\n                                                            </td>\n                                                        </tr>\n                                                        <tr>\n                                                            <td align=\"center\" class=\"esd-block-text es-p10t es-p5b\">\n                                                                <p>If you did not register with us, please disregard\n                                                                    this email.</p>\n                                                            </td>\n                                                        </tr>\n                                                        <tr>\n                                                            <td align=\"center\" class=\"esd-block-button es-p10t es-p10b\">\n                                                                <p style=\"font-size: 50px\">[code]</p>\n                                                            </td>\n                                                        </tr>\n                                                        </tbody>\n                                                    </table>\n                                                </td>\n                                            </tr>\n                                            </tbody>\n                                        </table>\n                                    </td>\n                                </tr>\n                                </tbody>\n                            </table>\n                        </td>\n                    </tr>\n                    </tbody>\n                </table>\n            </td>\n        </tr>\n        </tbody>\n    </table>\n</div>\n</body>\n\n</html>";
        var titleBody = body.Replace("[title]", title);
        var contentBody = titleBody.Replace("[body]", content);
        var usernameBody = contentBody.Replace("[username]", username);
        var codeBody = usernameBody.Replace("[code]", code);
        return codeBody;
    }
}